import React, { useState } from 'react';

const ProductCard = ({ product, renderStars }) => {
  const [isAdded, setIsAdded] = useState(false);

  const handleAddToCart = () => {
    setIsAdded(true);
    setTimeout(() => {
      setIsAdded(false);
    }, 2000);
  };

  return (
    <div className="card">
      {product.badge && (
        <div className="card__badge">{product.badge}</div>
      )}
      <img src={product.image} alt={product.title} />
      <div className="card__body">
        <div className="card__platform">{product.platform}</div>
        <h3 className="card__title">{product.title}</h3>
        <div className="card__rating">
          <span className="stars">{renderStars(product.rating)}</span>
          <span>({product.rating}) {product.reviews.toLocaleString()} rese√±as</span>
        </div>
        <div className="card__price">
          <div>
            <div className="price">${product.price.toLocaleString()} MXN</div>
            {product.oldPrice && (
              <div className="price--old">${product.oldPrice.toLocaleString()} MXN</div>
            )}
          </div>
        </div>
        <div className="card__actions">
          <button 
            className="btn btn--primary" 
            style={{flex: 1}}
            onClick={handleAddToCart}
            style={{
              flex: 1,
              background: isAdded ? 'var(--success)' : 'var(--primary)'
            }}
          >
            {isAdded ? '‚úì Agregado' : 'üõí Agregar'}
          </button>
          <button className="btn btn--ghost">‚ù§Ô∏è</button>
        </div>
      </div>
    </div>
  );
};

export default ProductCard;
