import React from 'react';

const Cart = () => {
  const cartItems = [
    {
      id: 1,
      title: 'Super Mario Odyssey',
      price: 1300,
      quantity: 1,
      image: 'https://tse4.mm.bing.net/th/id/OIP.Z52BIGCQi2mFHam42kxmpQHaEK?cb=ucfimg2ucfimg=1&rs=1&pid=ImgDetMain&o=7&rm=3'
    }
  ];

  const subtotal = cartItems.reduce((total, item) => total + (item.price * item.quantity), 0);
  const pointsToEarn = Math.floor(subtotal * 0.1); // 10% de puntos

  return (
    <section id="carrito" className="section container">
      <h2>üõí Carrito de Compras</h2>
      <div className="cart">
        <h3>üéÆ Tus productos ({cartItems.length} art√≠culo{cartItems.length !== 1 ? 's' : ''})</h3>
        
        {cartItems.map((item) => (
          <div key={item.id} className="cart-item">
            <img src={item.image} alt={item.title} />
            <div className="cart-item__info">
              <h4 className="cart-item__title">{item.title}</h4>
              <div className="cart-item__price">
                ${item.price.toLocaleString()} MXN x{item.quantity}
              </div>
            </div>
            <button className="btn btn--ghost">üóëÔ∏è</button>
          </div>
        ))}
        
        <div className="cart-total">
          <p>Subtotal: ${subtotal.toLocaleString()} MXN</p>
          <p>Puntos que ganar√°s: ‚≠ê +{pointsToEarn} puntos</p>
          <p><strong>Total: ${subtotal.toLocaleString()} MXN</strong></p>
          <button className="btn btn--primary">üí≥ Proceder al pago</button>
        </div>
      </div>
    </section>
  );
};

export default Cart;
